const allItems = $input.all();
const seen = new Set();
const filtered = [];

for (const item of allItems) {
  const title = (item.json.title || '').trim();
  const link = item.json.link || item.json.url || item.json.guid || '';
  if (!title) continue;

  const key = title.toLowerCase().replace(/[^a-z0-9ê°€-í£]/g, '').slice(0, 60);

  // ê°™ì€ ë°°ì¹˜ ë‚´ ì¤‘ë³µ
  if (seen.has(key)) continue;
  seen.add(key);

  // 24ì‹œê°„ ì´ë‚´ ê¸°ì‚¬ë§Œ
  const pubDate = item.json.published || '';
  if (pubDate) {
    const articleDate = new Date(pubDate);
    const now = new Date();
    const hoursDiff = (now - articleDate) / (1000 * 60 * 60);
    if (hoursDiff > 24) continue;
  }

  filtered.push({
    json: {
      title: title,
      link: link,
      source: item.json.source || 'unknown',
      published: pubDate,
      snippet: (item.json.snippet || '').slice(0, 500)
    }
  });
}

return filtered;

